import {spawn} from 'child_process';
import {createServer} from 'net';
import {join} from 'path';
import {Observable} from 'rxjs';
import superagent from 'superagent';

/**
 * Removes comments and whitespace from a PHP script, by calling a Web service.
 */
export class FastTransformer {

  /**
   * The default address that the server is listening on.
   * @type {string}
   */
  static get DEFAULT_ADDRESS() {
    return '127.0.0.1';
  }

  /**
   * Initializes a new instance of the class.
   * @param {Minifier} minifier The instance providing access to the minifier settings.
   */
  constructor(minifier) {
    let handler = () => this.close().subscribe();

    /**
     * The instance providing access to the minifier settings.
     * @type {Minifier}
     */
    this._minifier = minifier;
    this._minifier.on('end', handler).on('error', handler);

    /**
     * The underlying PHP process.
     * @type {object}
     */
    this._phpServer = null;
  }

  /**
   * Value indicating whether the PHP process is currently listening.
   * @type {boolean}
   */
  get listening() {
    return Boolean(this._phpServer && typeof this._phpServer == 'object');
  }

  /**
   * Terminates the underlying PHP process: stops the server from accepting new connections. It does nothing if the server is already closed.
   * @return {Observable} Completes when the PHP process is finally terminated.
   */
  close() {
    return !this.listening ? Observable.of(null) : new Observable(observer => {
      this._phpServer.process.kill();
      this._phpServer = null;
      observer.next();
      observer.complete();
    });
  }

  /**
   * Starts the underlying PHP process: begins accepting connections. It does nothing if the server is already started.
   * @return {Observable<number>} The port used by the PHP process.
   */
  listen() {
    if (this.listening) return Observable.of(this._phpServer.port);

    return this._getPort().map(port => {
      let address = FastTransformer.DEFAULT_ADDRESS;
      let args = ['-S', `${address}:${port}`, '-t', join(__dirname, '../web')];
      this._phpServer = {address, port, process: spawn(this._minifier.binary, args)};
      return port;
    }).delay(1000);
  }

  /**
   * Processes a PHP script.
   * @param {string} script The path to the PHP script.
   * @return {Observable<string>} The transformed script.
   */
  transform(script) {
    return this.listen()
      .mergeMap(() => Observable.from(superagent
        .get(`http://${this._phpServer.address}:${this._phpServer.port}/index.php`)
        .query({file: script})))
      .map(res => res.text);
  }

  /**
   * Gets an ephemeral port chosen by the system.
   * @return {Observable<number>} A port that the server can listen on.
   */
  _getPort() {
    return new Observable(observer => {
      let server = createServer();
      server.unref();
      server.on('error', err => observer.error(err));

      server.listen(0, FastTransformer.DEFAULT_ADDRESS, () => {
        observer.next(server.address().port);
        server.close(() => observer.complete());
      });
    });
  }
}

import {normalize} from 'path';
import {sync as which} from 'which';
import {Minifier} from './minifier';

export * from './fast_transformer';
export * from './minifier';
export * from './safe_transformer';

/**
 * Create a new instance of the plug-in.
 * @param {object} [options] The plug-in options.
 * @return {Minifier} The newly created instance.
 */
export function phpMinify(options = {}) {
  let minifier = new Minifier(typeof options.binary == 'string' ? normalize(options.binary) : which('php'));
  if (typeof options.mode == 'string') minifier.mode = options.mode;
  if (typeof options.silent == 'boolean') minifier.silent = options.silent;
  return minifier;
}

import {Transform} from 'stream';

import {name as pkgName} from '../package.json';
import {FastTransformer} from './fast_transformer';
import {SafeTransformer} from './safe_transformer';

/**
 * Removes PHP comments and whitespace by applying the [`php_strip_whitespace()`](https://secure.php.net/manual/en/function.php-strip-whitespace.php) function.
 */
export class Minifier extends Transform {

  /**
   * Initializes a new instance of the class.
   * @param {string} [binary] The path to the PHP executable.
   */
  constructor(binary = 'php') {
    super({objectMode: true});

    /**
     * The path to the PHP executable.
     * @type {string}
     */
    this.binary = binary;

    /**
     * Value indicating whether to silent the plug-in output.
     * @type {boolean}
     */
    this.silent = false;

    /**
     * The instance used to process the PHP code.
     * @type {object}
     */
    this._transformer = new SafeTransformer(this);
  }

  /**
   * The transformation type.
   * @type {string}
   */
  get mode() {
    return this._transformer instanceof FastTransformer ? 'fast' : 'safe';
  }

  /**
   * Sets the transformation type.
   * @param {string} value The new transformation type.
   */
  set mode(value) {
    this._transformer = value == 'fast' ? new FastTransformer(this) : new SafeTransformer(this);
  }

  /**
   * Transforms input and produces output.
   * @param {File} file The chunk to transform.
   * @param {string} encoding The encoding type if the chunk is a string.
   * @param {function} callback The function to invoke when the supplied chunk has been processed.
   */
  _transform(file, encoding, callback) {
    if (!this.silent) console.log(`Minifying: ${file.path}`);

    this._transformer.transform(file.path).subscribe({
      error: err => callback(new Error(`[${pkgName}] ${err.message}`)),
      next: data => {
        file.contents = Buffer.from(data, encoding);
        callback(null, file);
      }
    });
  }
}

import {execFile} from 'child_process';
import {Observable} from 'rxjs';

/**
 * Removes comments and whitespace from a PHP script, by calling a PHP process.
 */
export class SafeTransformer {

  /**
   * Initializes a new instance of the class.
   * @param {Minifier} minifier The instance providing access to the minifier settings.
   */
  constructor(minifier) {

    /**
     * The instance providing access to the minifier settings.
     * @type {Minifier}
     */
    this._minifier = minifier;
  }

  /**
   * Processes a PHP script.
   * @param {string} script The path to the PHP script.
   * @return {Observable<string>} The transformed script.
   */
  transform(script) {
    const exec = Observable.bindNodeCallback(execFile, stdout => stdout);
    return exec(this._minifier.binary, ['-w', script], {maxBuffer: 10 * 1024 * 1024});
  }
}

'use strict';

import {expect} from 'chai';
import {after, describe, it} from 'mocha';
import {FastTransformer, Minifier} from '../src/index';

/**
 * @test {FastTransformer}
 */
describe('FastTransformer', function() {
  this.timeout(30000);

  /**
   * @test {FastTransformer#listening}
   */
  describe('#listening', () => {
    it('should return whether the server is listening', done => {
      let transformer = new FastTransformer(new Minifier);
      expect(transformer.listening).to.be.false;

      transformer.listen()
        .do(() => expect(transformer.listening).to.be.true)
        .mergeMap(() => transformer.close())
        .do(() => expect(transformer.listening).to.be.false)
        .subscribe(null, done, done);
    });
  });

  /**
   * @test {FastTransformer#transform}
   */
  describe('#transform()', () => {
    let script = 'test/fixtures/sample.php';
    let transformer = new FastTransformer(new Minifier);
    after(() => transformer.close().subscribe());

    it('should remove the inline comments', done => {
      transformer.transform(script).subscribe(output => {
        /* eslint-disable quotes */
        expect(output).to.contain("<?= 'Hello World!' ?>");
        /* eslint-enable quotes */
      }, done, done);
    });

    it('should remove the multi-line comments', done => {
      transformer.transform(script).subscribe(output => {
        expect(output).to.contain('namespace dummy; class Dummy');
      }, done, done);
    });

    it('should remove the single-line comments', done => {
      transformer.transform(script).subscribe(output => {
        expect(output).to.contain('$className = get_class($this); return $className;');
      }, done, done);
    });

    it('should remove the whitespace', done => {
      transformer.transform(script).subscribe(output => {
        expect(output).to.contain('__construct() { }');
      }, done, done);
    });
  });
});

'use strict';

import {expect} from 'chai';
import {describe, it} from 'mocha';
import {Minifier, phpMinify} from '../src/index';

/**
 * @test {phpMinify}
 */
describe('phpMinify()', () => {
  it('should return a `Minifier` instance', () => {
    expect(phpMinify()).to.be.instanceof(Minifier);
  });

  it('should properly initialize the minifier properties', () => {
    let minifier = phpMinify({
      binary: '/usr/local/bin/php',
      mode: 'fast',
      silent: true
    });

    expect(minifier.binary).to.equal(process.platform == 'win32' ? '\\usr\\local\\bin\\php' : '/usr/local/bin/php');
    expect(minifier.mode).to.equal('fast');
    expect(minifier.silent).to.be.true;
  });
});

'use strict';

import {expect} from 'chai';
import {after, describe, it} from 'mocha';
import File from 'vinyl';
import {FastTransformer, Minifier, SafeTransformer} from '../src/index';

/**
 * @test {Minifier}
 */
describe('Minifier', function() {
  this.timeout(60000);

  /**
   * @test {Minifier#mode}
   */
  describe('#mode', () => {
    it('should be `safe` if the underlying transformer is a `SafeTransformer` one', () => {
      let minifier = new Minifier;
      minifier._transformer = new SafeTransformer(minifier);
      expect(minifier.mode).to.equal('safe');
    });

    it('should be `fast` if the underlying transformer is a `FastTransformer` one', () => {
      let minifier = new Minifier;
      minifier._transformer = new FastTransformer(minifier);
      expect(minifier.mode).to.equal('fast');
    });

    it('should change the underlying transformer on value update', () => {
      let minifier = new Minifier;

      minifier.mode = 'fast';
      expect(minifier._transformer).to.be.instanceOf(FastTransformer);

      minifier.mode = 'safe';
      expect(minifier._transformer).to.be.instanceOf(SafeTransformer);
    });
  });

  /**
   * @test {Minifier#_transform}
   */
  describe('#_transform()', () => {
    let file = new File({path: 'test/fixtures/sample.php'});

    let minifier = new Minifier;
    minifier.silent = true;
    after(() => minifier.emit('end'));

    it('should remove the inline comments', done => {
      minifier._transform(file, 'utf8', (err, file) => {
        if (err) done(err);
        else {
          /* eslint-disable quotes */
          expect(file.contents.toString()).to.contain("<?= 'Hello World!' ?>");
          done();
          /* eslint-enable quotes */
        }
      });
    });

    it('should remove the multi-line comments', done => {
      minifier._transform(file, 'utf8', (err, file) => {
        if (err) done(err);
        else {
          expect(file.contents.toString()).to.contain('namespace dummy; class Dummy');
          done();
        }
      });
    });

    it('should remove the single-line comments', done => {
      minifier._transform(file, 'utf8', (err, file) => {
        if (err) done(err);
        else {
          expect(file.contents.toString()).to.contain('$className = get_class($this); return $className;');
          done();
        }
      });
    });

    it('should remove the whitespace', done => {
      minifier._transform(file, 'utf8', (err, file) => {
        if (err) done(err);
        else {
          expect(file.contents.toString()).to.contain('__construct() { }');
          done();
        }
      });
    });
  });
});

